parameters.jsCode:
// Standardize incoming data from different sources
const items = $input.all();
const standardizedTickets = [];

for (const item of items) {
  let ticket = {
    id: item.json.id || Date.now().toString() + Math.random().toString(36).substr(2, 9),
    customerEmail: '',
    customerName: '',
    subject: '',
    message: '',
    source: '',
    timestamp: new Date().toISOString(),
    priority: 'medium',
    status: 'new'
  };

  // Handle email source
  if (item.json.subject && item.json.from) {
    ticket.customerEmail = item.json.from;
    ticket.customerName = item.json.fromName || item.json.from.split('@')[0];
    ticket.subject = item.json.subject;
    ticket.message = item.json.text || item.json.html || '';
    ticket.source = 'email';
  }
  // Handle webhook/form source
  else if (item.json.email) {
    ticket.customerEmail = item.json.email;
    ticket.customerName = item.json.name || 'Unknown Customer';
    ticket.subject = item.json.subject || 'Support Request';
    ticket.message = item.json.message || item.json.description || '';
    ticket.source = 'web_form';
  }
  // Handle chat source
  else if (item.json.chat_message) {
    ticket.customerEmail = item.json.user_email || 'unknown@example.com';
    ticket.customerName = item.json.user_name || 'Chat User';
    ticket.subject = 'Chat Support Request';
    ticket.message = item.json.chat_message;
    ticket.source = 'chat';
  }
  // Generic fallback
  else {
    ticket.customerEmail = item.json.customerEmail || 'unknown@example.com';
    ticket.customerName = item.json.customerName || 'Unknown';
    ticket.subject = item.json.subject || 'Support Request';
    ticket.message = item.json.message || JSON.stringify(item.json);
    ticket.source = 'unknown';
  }

  standardizedTickets.push({ json: ticket });
}

return standardizedTickets;

