parameters.jsCode:
// Parse Gemini classification and merge with ticket data
const ticket = $('Parse Message').first().json;
let classification;

try {
  const geminiResponse = $json.candidates[0].content.parts[0].text;
  // Clean up response - remove markdown formatting if present
  const cleanedResponse = geminiResponse.replace(/```json\n?|```\n?/g, '').trim();
  classification = JSON.parse(cleanedResponse);
} catch (error) {
  console.log('Error parsing Gemini response:', error);
  // Fallback classification if AI response is malformed
  classification = {
    category: 'general',
    priority: 'medium',
    sentiment: 'neutral',
    intent: 'question',
    urgency_score: 5,
    can_auto_resolve: false
  };
}

// Merge classification with original ticket
const enrichedTicket = {
  ...ticket,
  category: classification.category || 'general',
  priority: classification.priority || 'medium',
  sentiment: classification.sentiment || 'neutral',
  intent: classification.intent || 'question',
  urgency_score: classification.urgency_score || 5,
  can_auto_resolve: classification.can_auto_resolve || false,
  ai_confidence: 0.85,
  processed_at: new Date().toISOString()
};

// Auto-escalate urgent tickets
if (classification.urgency_score >= 8 || classification.priority === 'urgent') {
  enrichedTicket.escalated = true;
  enrichedTicket.assignee = 'senior_support';
  enrichedTicket.status = 'escalated';
}

// Mark for auto-resolution if possible
if (classification.can_auto_resolve && classification.urgency_score < 7) {
  enrichedTicket.auto_resolve_candidate = true;
}

return { json: enrichedTicket };

