// Create urgent escalation ticket
const ticket = $('Process Classification').first().json;
const aiResponse = $('Generate Response with Gemini').first().json.candidates[0].content.parts[0].text;

const escalationData = {
  ...ticket,
  status: 'escalated',
  escalation_reason: 'High urgency detected by AI classification',
  escalated_at: new Date().toISOString(),
  assignee: 'senior_support_team',
  sla_deadline: new Date(Date.now() + 2 * 60 * 60 * 1000).toISOString(), // 2 hours
  ai_response: aiResponse,
  escalation_type: 'urgent'
};

return { json: escalationData };

